import React,{useState} from 'react'
import { createTheme, ThemeProvider } from '@mui/material/styles';
import {Box,CssBaseline,Grid,Paper, useMediaQuery } from '@mui/material';
import "../../../styles/adminStyles.css"
/* Definimos el tema general para el DashBoard */
let theme = createTheme({
    palette: {
        primary: {
          light: '#63ccff',
          main: '#009be5',
          dark: '#006db3',
        },
      },
      typography: {
        h5: {
          fontWeight: 500,
          fontSize: 26,
          letterSpacing: 0.5,
        },
      },
      shape: {
        borderRadius: 8,
      },
      components: {
        MuiTab: {
          defaultProps: {
            disableRipple: true,
          },
        },
      },
      mixins: {
        toolbar: {
          minHeight: 48,
        },
      },
    });
    
    theme = {
      ...theme,
      components: {
        MuiDrawer: {
          styleOverrides: {
            paper: {
              backgroundColor: '#081627',
            },
          },
        },
        MuiButton: {
          styleOverrides: {
            root: {
              textTransform: 'none',
            },
            contained: {
              boxShadow: 'none',
              '&:active': {
                boxShadow: 'none',
              },
            },
          },
        },
        MuiTabs: {
          styleOverrides: {
            root: {
              marginLeft: theme.spacing(1),
            },
            indicator: {
              height: 3,
              borderTopLeftRadius: 3,
              borderTopRightRadius: 3,
              backgroundColor: theme.palette.common.white,
            },
          },
        },
        MuiTab: {
          styleOverrides: {
            root: {
              textTransform: 'none',
              margin: '0 16px',
              minWidth: 0,
              padding: 0,
              [theme.breakpoints.up('md')]: {
                padding: 0,
                minWidth: 0,
              },
            },
          },
        },
        MuiIconButton: {
          styleOverrides: {
            root: {
              padding: theme.spacing(1),
            },
          },
        },
        MuiTooltip: {
          styleOverrides: {
            tooltip: {
              borderRadius: 4,
            },
          },
        },
        MuiDivider: {
          styleOverrides: {
            root: {
              backgroundColor: 'rgb(255,255,255,0.15)',
            },
          },
        },
        MuiListItemButton: {
          styleOverrides: {
            root: {
              '&.Mui-selected': {
                color: '#4fc3f7',
              },
            },
          },
        },
        MuiListItemText: {
          styleOverrides: {
            primary: {
              fontSize: 14,
              fontWeight: theme.typography.fontWeightMedium,
            },
          },
        },
        MuiListItemIcon: {
          styleOverrides: {
            root: {
              color: 'inherit',
              minWidth: 'auto',
              marginRight: theme.spacing(2),
              '& svg': {
                fontSize: 20,
              },
            },
          },
        },
        MuiAvatar: {
          styleOverrides: {
            root: {
              width: 32,
              height: 32,
            },
          },
        },
      },
    };
/* Ancho de la pantalla que se consigurará */
const drawerWidth = 256;
const HomeAdmin = () => {
    const[ResponsiveMobile,setResponsiveMobile] = useState(false);
    /* Permite cambiar mediante un breakPoint las dimensiones */
    const SmHabilitado = useMediaQuery(theme.breakpoints.up('sm'));
    return (
        <ThemeProvider>
            <Box sx={{ display: 'flex', minHeight: '100vh' }}>
            <CssBaseline/>
            <Box
                component="nav"
                sx={{ width: { sm: drawerWidth}, flexShrink: {sm:0}    }}
            >
                {/* Evaluamos el responsive design de los SM o XS del dashboard */}
                {
                    SmHabilitado ? null : (
                        <Navigator
                            PaperProps={{ style:{ width: drawerWidth} }}
                            sx={{ display:{ sm:'block', xs:'none' } }}
                        />
                )}


            </Box>

            </Box>
        </ThemeProvider>
    )
}
export default HomeAdmin
