import React,{useState,Fragment} from 'react'
import {Input,Button,Card,CardContent,Typography,TextField,Grid,Autocomplete,Box} from "@mui/material";
import { alpha, styled } from "@mui/material/styles";

const FormularioAlumno = () => {
    const endPoint ="http://localhost:10000/ucn/formulario"
    //State
    const [formulario, setFormulario] = useState({
      rut: '',
      nombre: '',
      correo: '',
      carrera: '',
      cantidad: 0,
      electivo1: '',
      electivo2: '',
      electivo3: '',
    })

    const color_gris_60 = "rgba(60, 60, 60, 0.1)"
    const color_green_border = "#00FF87"
    const color_white = "white"
    
    const CssTextField = styled(TextField)({
        "& label.Mui-focused": {
          color: "white",
          fontSize: 12
        },
        "& .MuiInputBase-input": {
            backgroundColor: color_gris_60,
            color: "white",
            fontSize: 12
        },
        "& .MuiInput-underline:after": {
          //borderBottomColor: "white"
        },
        "& .MuiOutlinedInput-root": {
            color: "white",
            backgroundColor: color_gris_60,
            "& fieldset": {
                //borderColor: "white",
                color: "white",
            },
            "&:hover fieldset": {
                borderColor: color_white,
                color: "white"
            },
            "&.Mui-focused fieldset": {
                borderColor: color_green_border,
            },
            "& input:invalid": {
                borderColor: "red",
                borderWidth: 2
            }
        
        }
      });


    //Valores select
    const cantidad = [
        { label: '1', num: 1 },
        { label: '2', num: 2 },
        { label: '3', num: 3 }
      ];
    //Carreras que vienen desde la api
    const carrera = [
        { label: 'ICCI', sigla: 'ICCI' },
        { label: 'ICI', sigla: 'ICI' },
        { label: 'ITI', sigla: 'ITI' }
      ];
   //Electivos que vienen desde la api
   const electivos = [
    { label: 'Electivo 1', sigla: 'E1' },
    { label: 'Electivo 2', sigla: 'E2' },
    { label: 'Electivo 3', sigla: 'E3' }
  ];
  //Enviar datos
  const [selectedOptions, setSelectedOptions] = useState([]);

  const handleChange = (event, value) => setSelectedOptions(value);
  
  const handleSubmit = () => console.log(selectedOptions);
  
    return (
        <Box
        display="flex"
        justifyContent="center"
        alignItems="center"
        minHeight="80vh"
        
        >
        <Grid xs={12} sm={6} item 
        alignItems='center'
        justify='center'
        style={{
            boxShadow:`${alpha("#00FF87  ", 1)} 0 0 0 2px`,
            borderColor: "#00FF87" }}
            >
           
            <form>
                <Card sx={{ bgcolor: 'rgba(60, 60, 60, 0.6)', color: 'text.primary', p: 2 }}>
                    <Typography color ="white" gutterBottom variant ="h3" align="center" align="center">Formulario Alumno</Typography>
                    <CardContent>
                        <Grid container spacing ={1}>
                            <Grid xs={12} sm={6 } color= "green" item>
                            <CssTextField style={{ backgroundColor: 'rgba(160, 160, 160, 0.6)'}}label="Rut" placeholder="Ingrese Rut" name="rut" variant="outlined" fullWidth required
                            ></CssTextField>
                            </Grid>
                            <Grid xs={12} sm={6}item>
                            <CssTextField style={{ backgroundColor: 'rgba(160, 160, 160, 0.6)'}} label="Nombre" placeholder="Ingrese Nombre" name="nombre" variant="outlined" fullWidth required></CssTextField>
                            </Grid>
                            <Grid xs={12}item>
                            <CssTextField style={{ backgroundColor: 'rgba(160, 160, 160, 0.6)'}} type="email" label="Correo" placeholder="Ingrese alumnos.ucn.cl" name="correo" variant="outlined" fullWidth required></CssTextField>
                            </Grid>
                            <Grid xs={6}  item>
                                <Autocomplete 
                                        style={{ backgroundColor: 'rgba(160, 160, 160, 0.6)'}} 
                                        disablePortal
                                        id="carrera"
                                        options={carrera}
                                        onChange={(event, value) => console.log(value)} // prints the selected value
                                        renderInput={params => (
                                            <CssTextField {...params} label="Carrera" variant="outlined" fullWidth />
                                        )}
                                        required
                                />
                            </Grid>
                            <Grid xs={6}  item>
                                <Autocomplete
                                       style={{ backgroundColor: 'rgba(160, 160, 160, 0.6)'}}
                                        disablePortal
                                        id="cantidad"
                                        options={cantidad}
                                        onChange={(event, value) => console.log(value)} // prints the selected value
                                        renderInput={params => (
                                            <CssTextField {...params} label="Cantidad" variant="outlined" fullWidth />
                                        )}
                                        required
                                />
                            </Grid>
                            <Grid xs={12}  item>
                            <Autocomplete
                            style={{ backgroundColor: 'rgba(160, 160, 160, 0.6)', color:'white',borderColor:' rgba(20, 221, 175, 0.91)'}}
                                        disablePortal
                                        id="electivo1"
                                        options={electivos}
                                        
                                        onChange={(event, value) => console.log(value)} // prints the selected value
                                        renderInput={params => (
                                            <CssTextField {...params} label="Electivo 1" variant="outlined" fullWidth />
                                        )}
                                        required
                            />
                            </Grid>
                            <Grid xs={12} item>
                            <Autocomplete
                                        style={{ backgroundColor: 'rgba(160, 160, 160, 0.6)'}}
                                        disablePortal
                                        id="electivo2"
                                        options={electivos}
                                        
                                        onChange={(event, value) => console.log(value)} // prints the selected value
                                        renderInput={params => (
                                            <CssTextField {...params} label="Electivo 2" variant="outlined" fullWidth />
                                        )}
                                        required
                            />
                            </Grid>
                            <Grid xs={12} item>
                            <Autocomplete
                                        style={{ backgroundColor: 'rgba(160, 160, 160, 0.6)'}}
                                        disablePortal
                                        id="electivo3"
                                        options={electivos}
                                    
                                        onChange={(event, value) => console.log(value)} // prints the selected value
                                        renderInput={params => (
                                            <CssTextField {...params} label="Electivo 3" variant="outlined" fullWidth />
                                        )}
                                        required
                            />
                            </Grid>
                            <Grid xs={12} item>
                            <Button  color="primary" onClick={handleSubmit} type="submit" variant="contained">Enviar</Button>
                            
                            </Grid>
                        </Grid>



                    </CardContent>
                </Card>
            </form>
        </Grid>
        </Box>
    )
}
export default FormularioAlumno
