import React,{useState} from 'react'
/* import Select from "react-select" */
import { useForm } from "react-hook-form";

import { Fragment } from 'react'



const defaultValues = {
    select: "",
  };
const FormularioAlumno = () => {

  //State
  const [formulario, setFormulario] = useState({
    rut: '',
    nombre: '',
    correo: '',
    carrera: '',
    cantidad: '',
    electivo1: '',
    electivo2: '',
    electivo3: '',
  })
  const handleInputChange = (event) => {
        setFormulario({
            ...formulario,
            [event.target.nombre] : event.target.value
        })
    }
    const enviarDatos = (event) => {
        event.preventDefault()
        console.log('enviando datos...' + formulario.rut + ' ' + formulario.nombre)
    }


    //UserForm
    const {control, register, handleSubmit, formState:{ errors } } =  useForm({defaultValues});
    const onSubmit = (data, e)=>{
        //console.log(data)
      
        //dataPostFormulario.rut = data.rut
        e.target.reset()
    }
      
    return (
            <Fragment>
            <form onSubmit={enviarDatos}>
            <div className="form-row">
            <div class="form-group col-md-6">
            <br/>
            <input
                    placeholder="Rut"
                    class="form-control"
                    name="rut"
                    {...register("rut", { 
                    required:{
                        value: true,
                        message: 'Campo requerido' 
                    }
                })}
                    onChange = {
                        (e) => set
                    }
            />
            <span className="">
                {errors.rut &&  errors.rut.message}
            </span>
            </div>
            <br/>
            <input  
                    placeholder="Correo"
                    class="form-control"
                    
                    name="correo"
                    {...register("correo", { 
                    required:{
                        value: true,
                        message: 'Campo requerido' 
                    }
                })}
                onChange={handleInputChange}   
            />
            <span className="">
                {errors.correo &&  errors.correo.message}
            </span>
            <br/>
            <div class="form-group col-md-6">  
            <select {...register("Carrera",{ required: "Campo requerido" })} name="select" placeholder="select" value="sebita"
            onChange={handleInputChange}
            
            >
                
                <option value="carrera-icci" >ICCI</option>
                <option value="carrera-ici">ICI</option>
                <option value="carrera-iti">ITI</option>
                
            </select>
            <span className="">
                {errors.Carrera &&  errors.Carrera.message}
            </span>
            </div>
            <br/>
            <div class="form-group col-md-6">  
            <select  {...register("Cantidad",{ required: "Campo requerido"})}>
                <option value="1" >1</option>
                <option value="2">2</option>
                <option value="3">3</option>
            </select>
            <span className="">
                {errors.Cantidad &&  errors.Cantidad.message}
            </span>
            </div>
            <br/>
            <div class="form-group col-md-6">  
            <select  {...register("ESele1",{ required: "Campo requerido"})}>
                <option value="Electivo-1" >Electivo 1</option>
                <option value="Electivo-2">Electivo 2</option>
                <option value="Electivo-3">Electivo 3</option>
            </select>
            <span className="">
                {errors.ESele1 &&  errors.ESele1.message}
            </span>
            </div>
            <br/>
            <div class="form-group col-md-6">  
            <select  {...register("ESele2",{ required: "Campo requerido"})}>
                <option value="Electivo-1" >Electivo 1</option>
                <option value="Electivo-2">Electivo 2</option>
                <option value="Electivo-3">Electivo 3</option>
            </select>
            <span className="">
                {errors.ESele2 &&  errors.ESele2.message}
            </span>
            </div>
            <br/>
            <div class="form-group col-md-6">  
            <select  {...register("ESele3",{ required: "Campo requerido"})}>
                <option value="Electivo-1" >Electivo 1</option>
                <option value="Electivo-2">Electivo 2</option>
                <option value="Electivo-3">Electivo 3</option>
            </select>
            <span className="">
                {errors.ESele3 &&  errors.Cantidad.message}
            </span>
            </div>

            
            <br/>
        </div>



            <button type="submit" class="btn btn-primary" >Agregar</button>
        </form>
        </Fragment>

        );
}
export default FormularioAlumno
