import React from 'react'
import { useForm } from 'react-hook-form'
import { Fragment } from 'react'

const FormularioAlumno = (props) => {
    const dataPostFormulario = {
        rut: '',
        nombre: '',
        correo: '',
        carrera: '',
        cantidad: '',
        electivo1: '',
        electivo2: '',
        electivo3: '',
    }
    
    const [formulario, setFormulario] = React.useState(dataPostFormulario)
    
    //UseState
    const {register, handleSubmit, formState:{ errors } } = useForm();
    const onSubmit = (data, e)=>{
        //console.log(data)
        props.addUser(data)
        e.target.reset()
    }

    return (
            <Fragment>
            <form onSubmit={handleSubmit(onSubmit)}>
            <input
                    placeholder="Rut"
                    className=""
                    name="rut"
                    {...register("rut", { 
                    required:{
                        value: true,
                        message: 'Campo requerido' 
                    }
                })}   
            />
            <span className="">
                {errors.rut &&  errors.rut.message}
            </span>
            <br/>
            <input
                    placeholder="Correo"
                    className=""
                    name="correo"
                    {...register("correo", { 
                    required:{
                        value: true,
                        message: 'Campo requerido' 
                    }
                })}   
            />
            <span className="">
                {errors.correo &&  errors.correo.message}
            </span>
            <br/>
            <input
                    placeholder="Carrera"
                    className=""
                    name="username"
                    {...register("username", { 
                    required:{
                        value: true,
                        message: 'Campo requerido' 
                    }
                })}   
            />
            <select>
                <option value="grapefruit">Grapefruit</option>
                <option value="lime">Lime</option>
                <option selected value="carrera">carrera</option>
                <option value="mango">Mango</option>
            </select>
            
            <select {...register("gender")}>
                <option value="female">female</option>
                <option value="male">male</option>
                <option value="other">other</option>
            </select>

            <span className="">
                {errors.username &&  errors.username.message}
            </span>
            <br/>





            <button type="submit" className="" >Agregar</button>
        </form>
        </Fragment>

        );
}
export default FormularioAlumno
