import React from 'react'
import { useForm } from 'react-hook-form'
import { Fragment } from 'react'

const FormularioAlumno = (props) => {
    const dataPostFormulario = {
        rut: '',
        nombre: '',
        correo: '',
        carrera: '',
        cantidad: '',
        electivo1: '',
        electivo2: '',
        electivo3: '',
    }
    
    const [formulario, setFormulario] = React.useState(dataPostFormulario)
    
    //UseState
    const {register, handleSubmit, formState:{ errors } } = useForm();
    const onSubmit = (data, e)=>{
        //console.log(data)
        props.addUser(data)
        e.target.reset()
    }

    return (
            <Fragment>
            <form onSubmit={handleSubmit(onSubmit)}>
            <input
                    placeholder="Rut"
                    className=""
                    name="usuario"
                    {...register("name", { 
                    required:{
                        value: true,
                        message: 'Campo requerido' 
                    }
                })}   
            />
            <span className="">
                {errors.name &&  errors.name.message}
            </span>
            <br/>
            <input
                    placeholder="Correo"
                    className=""
                    name="username"
                    {...register("username", { 
                    required:{
                        value: true,
                        message: 'Campo requerido' 
                    }
                })}   
            />
            <span className="">
                {errors.username &&  errors.username.message}
            </span>
            <br/>
            <button type="submit" className="" >Agregar</button>
        </form>
        </Fragment>

        );
}
export default FormularioAlumno
