import React from 'react'
/* import Select from "react-select" */
import { v4 as uuidv4 } from 'uuid';
import { useForm } from "react-hook-form";

import { Fragment } from 'react'

//import Select from 'react-select'

const defaultValues = {
    select: "",
  };
const FormularioAlumno = (props) => {
    const dataPostFormulario = {
        rut: '',
        nombre: '',
        correo: '',
        carrera: '',
        cantidad: '',
        electivo1: '',
        electivo2: '',
        electivo3: '',
    }
    //State
    const [formulario, setFormulario] = React.useState(dataPostFormulario)
    //Agregar Formulario
    const addForm = (formi) =>{
        formulario.id=uuidv4()
        setFormulario([
        ...formulario,
            formi
        ])
    }
    //UserForm
    const {control, register, handleSubmit, formState:{ errors } } =  useForm({defaultValues});
    const onSubmit = (data, e)=>{
        //console.log(data)
        props.addUser(data)
        e.target.reset()
    }

   
      
    return (
            <Fragment>
            <form onSubmit={handleSubmit(onSubmit)}>
            <div className="form-row">
            <div class="form-group col-md-6">
            <br/>
            <input
                    placeholder="Rut"
                    class="form-control"
                    name="rut"
                    {...register("rut", { 
                    required:{
                        value: true,
                        message: 'Campo requerido' 
                    }
                })}   
            />
            <span className="">
                {errors.rut &&  errors.rut.message}
            </span>
            </div>
            <br/>
            <input  
                    placeholder="Correo"
                    class="form-control"
                    
                    name="correo"
                    {...register("correo", { 
                    required:{
                        value: true,
                        message: 'Campo requerido' 
                    }
                })}   
            />
            <span className="">
                {errors.correo &&  errors.correo.message}
            </span>
            <br/>
            <div class="form-group col-md-6">  
            <select {...register("Carrera",{ required: "Campo requerido" })} name="select">
                <option value="carrera-icci" >ICCI</option>
                <option value="carrera-ici">ICI</option>
                <option value="carrera-iti">ITI</option>
                <option selected value="coconut">""</option>
            </select>
            <span className="">
                {errors.Carrera &&  errors.Carrera.message}
            </span>
            </div>
            <br/>
            <div class="form-group col-md-6">  
            <select  {...register("Cantidad",{ required: "Campo requerido"})}>
                <option value="1" >1</option>
                <option value="2">2</option>
                <option value="3">3</option>
            </select>
            <span className="">
                {errors.Cantidad &&  errors.Cantidad.message}
            </span>
            </div>
            <br/>
            <div class="form-group col-md-6">  
            <select  {...register("ESele1",{ required: "Campo requerido"})}>
                <option value="Electivo-1" >Electivo 1</option>
                <option value="Electivo-2">Electivo 2</option>
                <option value="Electivo-3">Electivo 3</option>
            </select>
            <span className="">
                {errors.ESele1 &&  errors.ESele1.message}
            </span>
            </div>
            <br/>
            <div class="form-group col-md-6">  
            <select  {...register("ESele2",{ required: "Campo requerido"})}>
                <option value="Electivo-1" >Electivo 1</option>
                <option value="Electivo-2">Electivo 2</option>
                <option value="Electivo-3">Electivo 3</option>
            </select>
            <span className="">
                {errors.ESele2 &&  errors.ESele2.message}
            </span>
            </div>
            <br/>
            <div class="form-group col-md-6">  
            <select  {...register("ESele3",{ required: "Campo requerido"})}>
                <option value="Electivo-1" >Electivo 1</option>
                <option value="Electivo-2">Electivo 2</option>
                <option value="Electivo-3">Electivo 3</option>
            </select>
            <span className="">
                {errors.ESele3 &&  errors.Cantidad.message}
            </span>
            </div>

            
            <br/>
        </div>



            <button type="submit" class="btn btn-primary" >Agregar</button>
        </form>
        </Fragment>

        );
}
export default FormularioAlumno
