import React from 'react'
/* import Select from "react-select" */
import { useForm, Controller } from "react-hook-form";

import { Fragment } from 'react'

const FormularioAlumno = (props) => {
    const dataPostFormulario = {
        rut: '',
        nombre: '',
        correo: '',
        carrera: '',
        cantidad: '',
        electivo1: '',
        electivo2: '',
        electivo3: '',
    }
    
    const [formulario, setFormulario] = React.useState(dataPostFormulario)
    
    //UseState
    const {control, register, handleSubmit, formState:{ errors } } =  useForm();
    const onSubmit = (data, e)=>{
        //console.log(data)
        props.addUser(data)
        e.target.reset()
    }

    const defaultValues = {
        select: "",
        input: ""
      };
      
    return (
            <Fragment>
            <form onSubmit={handleSubmit(onSubmit)}>
            <div className="form-row">
            <div class="form-group col-md-6">
            <input
                    placeholder="Rut"
                    class="form-control"
                    name="rut"
                    {...register("rut", { 
                    required:{
                        value: true,
                        message: 'Campo requerido' 
                    }
                })}   
            />
            <span className="">
                {errors.rut &&  errors.rut.message}
            </span>
            </div>
            <br/>
            <input  
                    placeholder="Correo"
                    class="form-control"
                    
                    name="correo"
                    {...register("correo", { 
                    required:{
                        value: true,
                        message: 'Campo requerido' 
                    }
                })}   
            />
            <span className="">
                {errors.correo &&  errors.correo.message}
            </span>
            <br/>


            <select {...register("Carrera",{ required: "Campo requerido"})}>
                <option value="carrera-icci" >ICCI</option>
                <option value="carrera-ici">ICI</option>
                <option value="carrera-iti">ITI</option>
            </select>


            <span className="">
                {errors.Carrera &&  errors.Carrera.message}
            </span>
            <br/>
        </div>

            <button type="submit" class="btn btn-primary" >Agregar</button>
        </form>
        </Fragment>

        );
}
export default FormularioAlumno
